import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CartService } from '../../services/cart.service';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  cartCount$: Observable<number>;
  searchQuery: string = '';
  isCartOpen: boolean = false;

  constructor(
    private router: Router,
    public cartService: CartService
  ) {
    this.cartCount$ = this.cartService.cartItems$.pipe(
      map(() => this.cartService.getCartCount())
    );
  }

  ngOnInit(): void {
  }

  onSearch() {
    // Implement search functionality
    console.log('Search:', this.searchQuery);
  }

  openCart() {
    this.isCartOpen = true;
    this.cartService.isCartOpen = true;
  }
}

<header>
    <div class="nav container">
        <a routerLink="/" class="logo">JD Sport</a>

        <nav class="menu">
            <ul>
                <li><a routerLink="/" routerLinkActive="active">Home</a></li>
                <li><a routerLink="/shop" routerLinkActive="active">Shop</a></li>
                <li><a routerLink="/about" routerLinkActive="active">About Us</a></li>
                <li><a routerLink="/contact" routerLinkActive="active">Contact</a></li>
            </ul>
        </nav>

        <div class="search">
            <input type="text" placeholder="Search..." class="search-input" [(ngModel)]="searchQuery" (keyup.enter)="onSearch()">
            <button class="search-button" (click)="onSearch()">
                <i class='bx bx-search'></i>
            </button>
        </div>

        <i class='bx bxs-cart-alt' id="cart-icon" [attr.data-quantity]="cartCount$ | async" (click)="openCart()"></i>

        <app-cart></app-cart>
    </div>
</header>
